<?phpif(empty($_POST["login"]) === false && empty($_POST['pass']) === false){	$login = $register->fieldFilter($_POST['login']);	$password = $register->fieldFilter($_POST['pass']);		$check = db::query("SELECT user_id, AccountType FROM users WHERE login='".$login."' AND password='".md5($password)."'");			if(mysql_num_rows($check)>0)	{		while($log  = db::fetch($check)){			$userid = $log['user_id'];			$AccountType = $log['AccountType'];		}		$_SESSION['login'] = $login;		$_SESSION['id']    = $userid;		$_SESSION['Type']  = $AccountType;		header("Location: index.php");	} else {		echo '<p align=center><font size=2 color=red>Login lub has³o jest nieprawid³owe!</font></p>';	}	} else {	echo '<p align=center><font size=2 color=red>Wype³nij wszystkie pola!</font></p>';}?>